import React from "react";
import { useState } from "react";
import { ToastContainer, toast } from 'react-toastify';

const MainCart = (props) => {

    const serializedState = localStorage.getItem('state');
    const statedata = JSON.parse(serializedState);

    let state2 = { default: 0 };

    const [count, setCount] = useState(state2);
    
  const showToast = (type, curItem) => {
    // console.log(type);
    if (props.data.cartItems.length >= 0) {
      if (type == 'add') {
        if (props.data.cartItems.length == 0 || props.data.cartItems.length > 0) {
          toast.success('Item Added');
        }
      } else {
        const theItem = props.data.cartItems.find(product => product.product_id === curItem.product_id);
        if (theItem) {
          toast.error('Item Removed');
        }
      }
    }

  };

  const handleClick = (index, type) => {

    if (type == 'add') {
      setCount(state2 => {
        const newState = { ...state2 } //keep state immutable
        !newState[index] && (newState[index] = 0)
        newState[index]++
        return newState
      });
    } else {
      setCount(state2 => {
        const newState = { ...state2 } //keep state immutable
        !newState[index] && (newState[index] = 0)

        if (newState[index] > 0) {
          // console.log(newState[index]);
          newState[index]--
        }

        return newState
      });
    }

  };


    return (
        <div className="shopping-cart section">
            <div className="container">
                <div className="row">
                    <div className="col-12">

                        <table className="table shopping-summery">

                            <thead>
                                <tr className="main-hading">
                                    <th>PRODUCT</th>
                                    <th>NAME</th>
                                    <th className="text-center">UNIT PRICE</th>
                                    <th className="text-center">QUANTITY</th>
                                    <th className="text-center">TOTAL</th>
                                    <th className="text-center"><i className="ti-trash remove-icon"></i></th>
                                </tr>
                            </thead>

                            <tbody>

                                {
                                    statedata.cartItems.map((curItem) => {
                                        //  console.log('curItem');
                                        // console.log(curItem.name);
                                        return (
                                            <tr>
                                                <td className="image" data-title="No"><img src="https://via.placeholder.com/70x70" alt="#" /></td>
                                                <td className="product-des" data-title="Description">
                                                    <p className="product-name"><a href="#">{curItem.name}</a></p>
                                                </td>
                                                <td className="price" data-title="Price"><span>${curItem.mrp} </span></td>
                                                <td className="qty" data-title="Qty">

                                                    <div  className="input-group">
                                                        
                                                        <div className="button minus">
                                                            <button type="button" className="btn btn-primary btn-number" disabled="disabled" data-type="minus" data-field="quant[1]">
                                                                <i className="ti-minus"></i>
                                                            </button>
                                                        </div>

                                                        <input type="text" name="quant[1]" className="input-number" data-min="1" data-max="100" value={curItem.quantity} />
                                                        <div className="button plus">
                                                            <button type="button" className="btn btn-primary btn-number" data-type="plus" onClick={() => {
                                                                                    showToast('minus', curItem);
                                                                                    handleClick(curItem.product_id, 'minus');
                                                                                    props.ramovefromocartHandler({ product_id: curItem.product_id })
                                                                                    }}
                                                                                    data-field="quant[1]">
                                                                <i className="ti-plus"></i>
                                                            </button>
                                                        </div>
                                                    </div>

                                                </td>
                                                <td className="total-amount" data-title="Total"><span>${curItem.quantity * curItem.mrp}</span></td>
                                                <td className="action" data-title="Remove"><a href="#"><i className="ti-trash remove-icon"></i></a></td>
                                            </tr>
                                        )

                                    })
                                }

                            </tbody>

                        </table>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <div className="total-amount">
                            <div className="row">
                                <div className="col-lg-8 col-md-5 col-12">
                                    <div className="left">
                                        <div className="coupon">
                                            <form action="#" target="_blank">
                                                <input name="Coupon" placeholder="Enter Your Coupon" />
                                                <button className="btn">Apply</button>
                                            </form>
                                        </div>
                                        <div className="checkbox">
                                            <label className="checkbox-inline" for="2"><input name="news" id="2" type="checkbox" /> Shipping (+10$)</label>
                                        </div>
                                    </div>
                                </div>

                                <div className="col-lg-4 col-md-7 col-12">
                                    <div className="right">
                                        <ul>
                                            <li>Cart Subtotal<span>$330.00</span></li>
                                            <li>Shipping<span>Free</span></li>
                                            <li>You Save<span>$20.00</span></li>
                                            <li className="last">You Pay<span>$ {statedata.totalAmount}</span></li>
                                        </ul>
                                        <div className="button5">
                                            <a href="#" className="btn">Checkout</a>
                                            <a href="#" className="btn">Continue shopping</a>
                                        </div>
                                    </div>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default MainCart;
