import { ADD_TO_CART } from "../../constants"
const initialState = {
  cardData: [],
  totalAmount: 0,
  totalItems: 0,
};

export default function cardItems(state = [], action) {

  switch (action.type) {
    case ADD_TO_CART:
      console.log(action.data.product_id);
      // console.log(state);

      if (state.length > 0) {
        console.log(state);
        let updatedCart = state.map((curElem) => {

          console.log(curElem.product_id);
          if (curElem.product_id === action.data.product_id) {
            console.log('matched');
            return 1
          }
          return [
            ...state,
            { cardData: action.data }
          ]
        });

        return [
          ...state,
          { cardData: updatedCart }
        ]


        // return [
