import React, { useCallback, useContext, useEffect, useRef } from "react";
import { useState } from "react";
import { products } from './products';
import logo from './logo.svg';
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Home = (props) => {
 // console.log(props);
  const counterState = localStorage.getItem('counter');
  let counterdata = JSON.parse(counterState);

  let [counterhome, setCount] = useState(counterdata);

  // useEffect(() => {
  //  setCount(counterdata => {
  //    console.log(`${counterdata['1706']}-${counterdata['1707']}-home`);
  //     const newState = { ...counterdata } //keep state immutable
  //     return newState
  //   });
  // });
  // console.log(count);

  // useEffect(() => {
  //   //When a changes we need to execute
  //   //specific logic for a and other vars of the state.
  //   //Which is not described here and that's the reason
  //   //why we need 2 different useEffects
  // ///setCount(counterdata);
  // }, [counterdata]);

  useState(() => {
    const serializedState = localStorage.getItem('state');
    const statedata = JSON.parse(serializedState);

    if (statedata) {
      //  console.log(statedata.data.cartItems);
      if (statedata.cartItems.length > 0) {
        props.data.cartItems = statedata.cartItems;
        props.data.totalAmount = statedata.totalAmount;
        props.data.totalItems = statedata.totalItems;
      }
    }

  })

  if (props.data.cartItems.length >= 0) {

    try {
      //  console.log(props.data);
      if (props.data.cartItems.length == 0) {
        // console.log('0');
        const serializedState = JSON.stringify(props.data);
        localStorage.setItem('state', serializedState);
      } else {
        const serializedState = JSON.stringify(props.data);
        localStorage.setItem('state', serializedState);
      }

    } catch (e) {
      // Ignore write errors;
    }

  }

  const handleClick = (index, type) => {

    if (type == 'add') {

      const product_count = localStorage.getItem('counter');
      const counter2 = JSON.parse(product_count);

      setCount(state2 => {
        const newState = counter2
        return newState
      });

      setCount(state2 => {
        //console.log(state2);
        const newState = { ...state2 } //keep state immutable
        !newState[index] && (newState[index] = 0)
        newState[index]++

        const product_count = JSON.stringify(newState);
        localStorage.setItem('counter', product_count);
        return newState
      });
    } else if (type == 'minus') {

      const product_count = localStorage.getItem('counter');
      const counter2 = JSON.parse(product_count);

      setCount(state2 => {
        const newState = counter2
        return newState
      });


      setCount(state2 => {
        const newState = { ...state2 } //keep state immutable
        !newState[index] && (newState[index] = 0)

        if (newState[index] > 0) {
          // console.log(newState[index]);
          newState[index]--
        }

        const product_count = JSON.stringify(newState);
        localStorage.setItem('counter', product_count);
        return newState
      });
    }

  };

  const showToast = (type, curItem) => {
    // console.log(type);
    if (props.data.cartItems.length >= 0) {
      if (type == 'add') {
        if (props.data.cartItems.length == 0 || props.data.cartItems.length > 0) {
          toast.success('Item Added');
        }
      } else {

        const theItem = props.data.cartItems.find(product => product.product_id === curItem.id);

        console.log(curItem);
        if (theItem) {
          toast.error('Item Removed');
        }
      }
    }

  };


  // console.log(counter2);

  // const product_count = JSON.stringify(counterhome);
  // localStorage.setItem('counter', product_count);

  // onClick(Event){
  //   showToast();
  // }

  // const level = props.data.cartItems;
  //   const [rows,setRows] = useState({
  //     level
  //   });

  //   React.useEffect(()=>{
  //     console.log(rows);
  //     const data = localStorage.setItem('newData',JSON.stringify(rows));
  //   });

  // const counter = localStorage.getItem('counter');
  // let counter2 = JSON.parse(counter);

  const url = 'https://jsonplaceholder.typicode.com/todos';

  let productcontent = null

  const [product, setProduct] = useState({
    data: null
  })

  useEffect(() => {
    // console.log('useEffect');
    // setProduct({
    //   data: null
    // })
    // console.log('props.data.search');
    // console.log(props.data.search);
    
    if (props.data.search) {
      const result = products.filter((product) => {
				switch (value) {
					case '20-50':
						return product.mrp <= 20 && product.mrp > 10;
						break;
					case '20-50':
						return product.mrp <= 50 && product.mrp > 21;
						break;
					case '50-100':
						return product.mrp <= 101 && product.mrp > 51;
						break;
					default:
						return product.mrp > 102;
				}

			});

      setProduct(product);
    }else{
      console.log('else');
      axios.get(url).then(response => {
        //   console.log(response.data);
        setProduct({
          data: response.data
        });
      }).catch((err) => {
        console.log(err);
      });
    }

  }, [])

  //console.log(product);


  const counter = localStorage.getItem('counter');
  let counter2 = JSON.parse(counter);

  if (product.data) {

    productcontent = product.data.map((curItem) => {
      // console.log(curItem.userId);
      return <div className="col-xl-3 col-lg-4 col-md-4 col-12" key={curItem.id}>
        <div className="single-product">
          <div className="product-img">
            <a>
              <img className="default-img" src={logo} alt="#" />
            </a>
            <div className="button-head">

              <div className="product-action">

                <div className="qty mt-5">
                  <button className="minus" onClick={() => {
                    showToast('minus', curItem);
                    handleClick(curItem.id, 'minus');
                    props.ramovefromocartHandler({ product_id: curItem.id })
                  }}>-</button>
                  <input type="text"
                    disabled={true}
                    value={counter2[curItem.id]}
                    className="countdown" />
                  <button className="plus btn-btn-primary" onClick={() => {
                    showToast('add', curItem);
                    handleClick(curItem.id, 'add');
                    props.addTocartHandler({ product_id: curItem.id, mrp: 15 + curItem.id, name: curItem.title, quantity: 1 });
                  }}>+</button>
                </div>

              </div>

              <div className="product-action-2">
              </div>
            </div>
          </div >
          <div className="product-content">
            <h3>
              <a href="#"> {curItem.title}</a>
              <a href="#"></a>
            </h3>
            <div className="product-price">
              <span>$  {15 + curItem.id}</span>
            </div>
          </div>
        </div >
      </div >
    })

  }


  // console.log('home');
  // console.log(counter2);

  return (

    <div>

      <ToastContainer
        position="top-center"
        title='success'
        autoClose={4000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />

      <section className="hero-slider">

        <div className="single-slider">
          <div className="container">
            <div className="row no-gutters">
              <div className="col-lg-9 offset-lg-3 col-12">
                <div className="text-inner">
                  <div className="row">
                    <div className="col-lg-7 col-12">
                      <div className="hero-text">
                        <h1><span>UP TO 50% OFF </span>Shirt For Man</h1>
