import React, { useCallback, useContext, useEffect, useRef } from "react";
import { useState } from "react";
import { products } from './products';
import logo from './logo.svg';
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Home = (props) => {

  // console.log('home');

  const counterState = localStorage.getItem('counter');
  let counterdata = JSON.parse(counterState);

  let [counterhome, setCount] = useState(counterdata);

  useState(() => {
    const serializedState = localStorage.getItem('state');
    const statedata = JSON.parse(serializedState);

    if (statedata) {
      //  console.log(statedata.data.cartItems);
      if (statedata.cartItems.length > 0) {
        props.data.cartItems = statedata.cartItems;
        props.data.totalAmount = statedata.totalAmount;
        props.data.totalItems = statedata.totalItems;
      }
    }

  })

  if (props.data.cartItems.length >= 0) {

    try {
      const serializedState = JSON.stringify(props.data);
      localStorage.setItem('state', serializedState);

      const test = JSON.stringify({ default: 0 });
      localStorage.setItem('counter', test);
    } catch (e) {
      // Ignore write errors;
    }

  }

  const handleClick = (index, type) => {

   // console.log(index);

    if (type == 'add') {

      const product_count = localStorage.getItem('counter');
      const counter2 = JSON.parse(product_count);

      setCount(state2 => {
        const newState = counter2
        return newState
      });

      setCount(state2 => {

        const newState = { ...state2 } //keep state immutable
        !newState[index] && (newState[index] = 0)
        newState[index]++
        console.log(newState);

        const product_count = JSON.stringify(newState);
        localStorage.setItem('counter', product_count);

        return newState
      });

    } else if (type == 'minus') {

      const product_count = localStorage.getItem('counter');
      const counter2 = JSON.parse(product_count);

      setCount(state2 => {
        const newState = counter2
        return newState
      });


      setCount(state2 => {
        const newState = { ...state2 } //keep state immutable
        !newState[index] && (newState[index] = 0)

        if (newState[index] > 0) {
          // console.log(newState[index]);
          newState[index]--
        }

        const product_count = JSON.stringify(newState);
        localStorage.setItem('counter', product_count);
        return newState
      });
    }

  };

  const showToast = (type, curItem) => {
    // console.log(type);
    if (props.data.cartItems.length >= 0) {
      if (type == 'add') {
        if (props.data.cartItems.length == 0 || props.data.cartItems.length > 0) {
          toast.success('Item Added');
        }
      } else {
        const theItem = props.data.cartItems.find(product => product.product_id === curItem.product_id);
        if (theItem) {
          toast.error('Item Removed');
        }
      }
    }

  };


  // console.log(counter2);

  // const product_count = JSON.stringify(counterhome);
  // localStorage.setItem('counter', product_count);

  // onClick(Event){
  //   showToast();
  // }

  // const level = props.data.cartItems;
  //   const [rows,setRows] = useState({
  //     level
  //   });

  //   React.useEffect(()=>{
  //     console.log(rows);
  //     const data = localStorage.setItem('newData',JSON.stringify(rows));
  //   });

  // console.log(props);.

  const counter = localStorage.getItem('counter');
  let counter2 = JSON.parse(counter);

  const url = 'https://jsonplaceholder.typicode.com/todos';

  let productcontent = null

  const [product, setProduct] = useState({
    data: null
  })

  useEffect(() => {
    // setProduct({
    //   data: null
    // })
    axios.get(url).then(response => {
      //   console.log(response.data);
      setProduct({
        data: response.data
      })

    }).catch((err) => {
      console.log(err);
    })

  }, [])

  //console.log(product);

  if (product.data) {

    productcontent = product.data.map((curItem) => {
      // console.log(curItem.userId);
      return <div className="col-xl-3 col-lg-4 col-md-4 col-12" key={curItem.id}>
        <div className="single-product">
          <div className="product-img">
            <a>
              <img className="default-img" src={logo} alt="#" />
            </a>
            <div className="button-head">

              <div className="product-action">

                <div className="qty mt-5">
                  <button className="minus" onClick={() => {
                    showToast('minus', curItem);
                    handleClick(curItem.id, 'minus');
                    props.ramovefromocartHandler({ product_id: curItem.id })
                  }}>-</button>
                  <input type="text"
                    disabled={true}
                    value={counter2[curItem.id]}
                    className="countdown" />
                  <button className="plus btn-btn-primary" onClick={() => {
                    showToast('add', curItem);
                    handleClick(curItem.id, 'add');
                    props.addTocartHandler({ product_id: curItem.id, mrp: 15 + curItem.id, name: curItem.title, quantity: 1 });
                  }}>+</button>
                </div>

              </div>

              <div className="product-action-2">
              </div>
            </div>
          </div >
          <div className="product-content">
            <h3>
              <a href="#"> {curItem.title}</a>
              <a href="#"></a>
            </h3>
            <div className="product-price">
              <span>$  {15 + curItem.id}</span>
            </div>
          </div>
        </div >
      </div >
    })

  }

  return (

    <div>

      <ToastContainer
        position="top-center"
        title='success'
        autoClose={4000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />

      <section className="hero-slider">

        <div className="single-slider">
          <div className="container">
            <div className="row no-gutters">
              <div className="col-lg-9 offset-lg-3 col-12">
                <div className="text-inner">
                  <div className="row">
                    <div className="col-lg-7 col-12">
                      <div className="hero-text">
                        <h1><span>UP TO 50% OFF </span>Shirt For Man</h1>
                        <p>
                          Maboriosam in a nesciung eget magnae <br />
                          dapibus disting tloctio in the find it pereri <br />
                          odiy maboriosm.
                        </p>
                        <div className="button">
                          <a href="#" className="btn">Shop Now!</a>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

      </section>

      <section className="small-banner section">
        <div className="container-fluid">
          <div className="row">

            <div className="col-lg-4 col-md-6 col-12">
              <div className="single-banner">
                <img src="https://via.placeholder.com/600x370" alt="#" />
                <div className="content">
                  <p>Man's Collectons</p>
                  <h3>
                    Summer travel <br />
                    collection
                  </h3>
                  <a href="#">Discover Now</a>
                </div>
              </div>
            </div>

            <div className="col-lg-4 col-md-6 col-12">
              <div className="single-banner">
                <img src="https://via.placeholder.com/600x370" alt="#" />
                <div className="content">
                  <p>Bag Collectons</p>
                  <h3>
                    Awesome Bag <br />
                    2020
                  </h3>
                  <a href="#">Shop Now</a>
                </div>
              </div>
            </div>

            <div className="col-lg-4 col-12">
              <div className="single-banner tab-height">
                <img src="https://via.placeholder.com/600x370" alt="#" />
                <div className="content">
                  <p>Flash Sale</p>
                  <h3>
                    Mid Season <br />
                    Up to <span>40%</span> Off
                  </h3>
                  <a href="#">Discover Now</a>
                </div>
              </div>
            </div>

          </div>
        </div>
      </section>

      <div className="product-area section">
        <div className="container">
          <div className="row">
            <div className="col-12">
              <div className="section-title">
                <h2>Trending Item</h2>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <div className="product-info">
                <div className="nav-main">
                  <ul className="nav nav-tabs" id="myTab" role="tablist">
                    <li className="nav-item">
                      <a className="nav-link active" data-toggle="tab" href="#man" role="tab">Man</a>
                    </li>
                    <li className="nav-item">
                      <a className="nav-link" data-toggle="tab" href="#women" role="tab">Woman</a>
                    </li>
                  </ul>
                </div>

                <div className="tab-content" id="myTabContent">

                  <div className="tab-pane fade show active" id="man" role="tabpanel">
                    <div className="tab-single">
                      <div className="row" id="mens">
                        <div className='col-md-12'>
                          <div className='row'>
                            {productcontent}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="tab-pane fade" id="women" role="tabpanel">
                    <div className="tab-single">
                      <div className="row" id="womens">

                      </div>
                    </div>
