import React, { useState, useEffect } from 'react'
import { ReactSearchAutocomplete } from 'react-search-autocomplete'
import axios from "axios";
import '../index.css';


const Search = () => {

  const [myOptions, setMyOptions] = useState([]);

  const [product, setProduct] = useState({
    data: null
  })

  let productcontent = null

  const url = 'https://pokeapi.co/api/v2/ability/?limit=20&offset=20';
  const url2 = 'https://jsonplaceholder.typicode.com/todos';

  useEffect(() => {

    axios.get(url).then(response => {
      //  console.log(response.data);
      setMyOptions({
        data: response.data.results
      });
    }).catch((err) => {
      console.log(err);
    });

    axios.get(url2).then(response => {
      //  console.log(response.data);
      setProduct({
        data: response.data
      })

    }).catch((err) => {
      console.log(err);
    });

  }, []);


  const handleOnSearch = (string, results) => {
    // onSearch will have as the first callback parameter
    // the string searched and for the second the results.
    //console.log(string, results)
    // axios.get(`https://makemydeals.co.in/api/product/search/${string}`).then(response => {
    //   console.log(response.data);
    //   setMyOptions({
    //     data: response.data
    //   })
    // }).catch((err) => {
    //   console.log(err);
    // });
  }

  const handleOnHover = (result) => {
    // the item hovered
    // console.log(result)
  }

  const handleOnSelect = (item) => {
    // the item selected
    // console.log(product);

    const result = product.data.filter((product) => {
      return product.title === 'delectus aut autem';
    })
    console.log(result);
    setProduct(result);

  }

  const handleOnFocus = () => {
    // console.log('Focused')
  }

  const formatResult = (item) => {
    // console.log(item);
    return (
      <>
        <a href={item.url} target="_blank"> <span style={{ display: 'block', textAlign: 'left' }}> {item.name}</span></a>
      </>
    )
  }


  if (product.data) {

    productcontent = product.data.map((curItem) => {
      // console.log(curItem.userId);
      return <div className="col-xl-3 col-lg-4 col-md-4 col-12" key={curItem.id}>
        <div className="single-product">
          <div className="product-img">
            <a>
              <img className="default-img" src={logo} alt="#" />
            </a>
            <div className="button-head">

              <div className="product-action">

                <div className="qty mt-5">
                  <button className="minus" onClick={() => {
                    showToast('minus', curItem);
                    handleClick(curItem.id, 'minus');
                    props.ramovefromocartHandler({ product_id: curItem.id })
                  }}>-</button>
                  <input type="text"
                    disabled={true}
                    value={counter2[curItem.id]}
                    className="countdown" />
                  <button className="plus btn-btn-primary" onClick={() => {
                    showToast('add', curItem);
                    handleClick(curItem.id, 'add');
                    props.addTocartHandler({ product_id: curItem.id, mrp: 15 + curItem.id, name: curItem.title, quantity: 1 });
                  }}>+</button>
                </div>

              </div>

              <div className="product-action-2">
              </div>
            </div>
          </div >
          <div className="product-content">
            <h3>
              <a href="#"> {curItem.title}</a>
              <a href="#"></a>
            </h3>
            <div className="product-price">
              <span>$  {15 + curItem.id}</span>
            </div>
          </div>
        </div >
      </div >
    })

  }

  // console.log(myOptions);

  return (
    <>
      <div style={{ width: 400 }}>
        <ReactSearchAutocomplete
          items={myOptions.data}
          onSearch={handleOnSearch}
          onHover={handleOnHover}
          onSelect={handleOnSelect}
          onFocus={handleOnFocus}
          autoFocus
          formatResult={formatResult}
        />
      </div>
    </>
  );

}

export default Search;
