export const reducer = (state, action) => {
  
    if (action.type === 'remove_item') {
        alert('hello');
        return {
           
            ...state,
            item: state.item.filter((curElem) => {
                return curElem.profuct_id !== action.payload;
            })
        }
    }

    return state;
}
