import { ADD_TO_CART } from "../../constants"
const initialState = {
  cartItems: [],
  totalAmount: 0,
  totalItems: 0,
};

export default function cardItems(state = initialState, action) {

  switch (action.type) {
    case ADD_TO_CART:
      // console.log('action');
      console.log(state.cartItems);

      const theItem = state.cartItems.find(product => product.product_id === action.data.product_id);

      // console.log(theItem);

      if (theItem) {
        console.log('matched');
        // return theItem.quantity += 1;
        // return {
        //   ...state,
        //   cartItems: [...state.cartItems, { theItem: action.data, quantity: action.quantity += 1 }],
        // };

        return {...state,cardItems:state.cartItems.map(item => item.id === action.product_id ? {...item,quantity:item.quantity+1,}:it)}
      } else {
        console.log('not matched');
        return {
          ...state,
          cartItems: [...state.cartItems, action.data],
        };
      }
      break;

    default:
      return state
  }
}
