import React, { useContext, useEffect, useState } from "react";
import Items from './items';
import logo from './logo.svg';
import { CartContext } from './App';

function Contextcart() {

    let content = null

    const { products } = useContext(CartContext);

    const url = 'https://makemydeals.co.in/api/products/category/86';

    const [product, setProduct] = useState({
        data: null
    })

    useEffect(() => {
        setProduct({
            data: null
        })

        axios.get(url).then(response => {
            setProduct({
                data: response.data
            })
        }).catch(() => {
            data: null
        })
    }, [url])

    if (product.data) {
        content =   <div className="col-xl-3 col-lg-4 col-md-4 col-12">
      <div className="single-product">
        <div className="product-img">
          <a >
            <img  className="default-img"  src="http://localhost/laravel/public/images/products/550x750.png"  alt="#" />
           </a>
          <div className="button-head">
            <div className="product-action">
              <a data-toggle="modal" data-target="#product_detail_modal" title="Quick View" href="#"><i className="ti-eye"></i><span>Quick Shop</span></a>
              <div className="qty mt-5">
                <button className="minus" >-</button>
                <input type="text" disabled={true} className="countdown" value={1} />
                <button className="plus btn-btn-primary">+</button>
              </div>
            </div>
            <div className="product-action-2">
            </div>
          </div>
        </div >
        <div className="product-content">
          <h3>
            <a href="#"> </a>
            <a href="#"></a>
          </h3>
          <div className="product-price">
            <span>$</span>
          </div>
        </div>
      </div >
    </div >

    }

    return (

        <div className='row'>
            {
                products.map((curItem) => {
                    if (curItem.quantity > 0) {
                        return <Items key={curItem.product_id} {...curItem} />
                    }
                })
            }
        </div>
    )

}

export default Contextcart;
