import React, { useCallback, useContext, useEffect, useRef } from "react";
import { useState } from "react";
import { products } from './products';
import logo from './logo.svg';
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Home = (props) => {

  // console.log('home');

  const counterState = localStorage.getItem('counter');
  let counterdata2 = JSON.parse(counterState);

  const test = JSON.stringify(counterdata2);
  localStorage.setItem('counter', test);

  const counterState2 = localStorage.getItem('counter');
