import React, { useContext } from "react";
import { CartContext } from "./App";


const Items = ({ product_id, product_name, mrp, quantity }) => {

  const { removeItem, increment, decrement } = useContext(CartContext);

  return (
    // <div className='card col-md-3'>
    //   <div className='card-header'>
    //     {product_name}
    //   </div>
    //   <div className='card-body'>
    //     {mrp} <br />
    //     <div className="counter">
    //       <span className="down" onClick={() => decrement(product_id)}>-</span>
    //       <input type="text" placeholder={quantity} />
    //       <span className="up" onClick={() => increment(product_id)} >+</span>
    //     </div>

    //     <button onClick={() => removeItem(product_id)} className="btn btn-danger">Delete</button>
    //   </div>
    // </div>

    <div class="col-xl-3 col-lg-4 col-md-4 col-12">
      <div class="single-product">
        <div class="product-img">
          <a href="product-details.html">
            <img
              class="default-img"
              src="http://localhost/laravel/public/images/products/550x750.png"
              alt="#"
            />
            <img
              class="hover-img"
              src="https://via.placeholder.com/550x750"
              alt="#"
            />
          </a>
          <div class="button-head">
            <div class="product-action">
              <a data-toggle="modal" data-target="#product_detail_modal"
              title="Quick View"
              href="#"><i class="ti-eye"></i
              ><span>Quick Shop</span></a
            >

            <div class="qty mt-5">
              <button className="minus bg-dark">-</button>
            <input type="text" disabled="true" class="countdown" value={quantity} />
            <button class="plus bg-dark">+</button>
        </div>
      </div>
      <div class="product-action-2">
      </div>
    </div>
                            </div >
  <div class="product-content">
    <h3>
      <a href="#"> {product_name}</a>
    </h3>
    <div class="product-price">
      <span>$  {mrp}</span>
    </div>
  </div>
                          </div >
                        </div >
  )
}

export default Items;
