// import { ADD_TO_CART } from "../../constants"

const initialState = {
  cartItems: [],
  totalAmount: 0,
  totalItems: 0,
};

export default function cardItems(state = initialState, action) {

  switch (action.type) {
    case 'ADD_TO_CART':
      // console.log(state.cartItems);
      const theItem = state.cartItems.find(product => product.product_id === action.data.product_id);
      if (theItem) {
        // console.log('matched');
        return { ...state, cartItems: state.cartItems.map(item => item.product_id === action.data.product_id ? { ...item, quantity: item.quantity + 1, } : item) }
      } else {
        // console.log('not matched');
        return {
          ...state,
          cartItems: [...state.cartItems, action.data],
        };
      }
      break;

    case 'REMOVE_FROM_CART':
      // console.log(state.cartItems);
      const theItem2 = state.cartItems.find(product => product.product_id === action.data.product_id);
      if (theItem2) {
        // console.log('matched');
        return { ...state, cartItems: state.cartItems.map(item => item.product_id === action.data.product_id ? { ...item, quantity: item.quantity - 1, } : item) }
      } else {
        // console.log('not matched');
        return {
          ...state,
          cartItems: [...state.cartItems, action.data],
        };
      }
      break;

    default:
      return state
  }
}
