import { ADD_TO_CART } from "../../constants"
const initialState = {
  cartItems: [],
  totalAmount: 0,
  totalItems: 0,
};

export default function cardItems(state = initialState, action) {

  switch (action.type) {
    case ADD_TO_CART:
     // console.log('action');
     // console.log(state.cartItems);

      const theItem = state.cartItems.find(product => product.product_id === action.data.product_id);

      console.log(theItem);

      if (theItem) {
      //  console.log('matched');

       // return theItem.quantity += 1;
        return {
          ...state,
          cartItems: [,{product_id: action.data.product_id, name: action.data.name, quantity: theItem.quantity += 1}],
        };
      }else{
        return {
          ...state,
          cartItems: [...state.cartItems, action.data],
        };
      }
      break;

    default:
      return state
  }
}
